import { useReducer } from "react";

function updateFunction(state, input) {
    return state + input;

}

function UseReducerHook() {
    const [initial, dispatch] = useReducer(updateFunction, 0);
    return (
        <>
            <h3>Update counter value using useReducer in React</h3>
            <h4> current value is : {initial}</h4>
            <button onClick={() => {
                dispatch(+1)
            }}> click to increment</button>
            <button onClick={() => {
                dispatch(-1)
            }}> click to decrement</button>
        </>
    )
}

export default UseReducerHook;


