import React, {useReducer} from "react";

const FC = () =>{
    const reducerFunction = (state, action)=>{
        if(action === 'reset') return 0;
        return state + action;
    }
    let [counter, dispatch] = useReducer(reducerFunction, 0);
    return(
        <>
            <h2>The current value of the counter is : {counter}</h2>
            <button onClick={()=>{
                dispatch(+1)
            }}>INC</button>
            <button onClick={()=>{
                dispatch(-1)
            }}>DEC</button>
            <button onClick={()=>{
                dispatch(+1)
            }}>INC</button>
        </>
    )
}

export default FC;
