// functional component, classs component
// passing prop, default prop, prop check
// conditional rendering( ternary operator, if, ampercend)
// list and keys
// useState, useReducer, useEffect, useRef, useContext, useCallback, useMemo
// controlled and uncontrolled component
// api call axios and useeffect (ajax call)
// create form and show form data
// custom hook (api calling)
// toggle
// lazy loading
// useCallback hook performence optimization technique


import React, { useState } from "react";

const FC = () => {
  const [todos, setTodos] = useState([])
  const [text, setText] = useState('')
  const [editingId, seteditingId] = useState(null)
  const [editingText, setEditingText] = useState('')

  const handleAdd = () => {
    if (text.trim()) {
      const newTodo = { id: Date.now(), text }
      setTodos([...todos, newTodo])
      setText('')
    }
  }

   // Delete a todo
   const handleDelete = (id) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  // Start editing a todo
  const handleEdit = (id, currentText) => {
    setEditingId(id);
    setEditingText(currentText);
  };

  // Save the edited todo
  const handleSave = (id) => {
    const updatedTodos = todos.map(todo =>
      todo.id === id ? { ...todo, text: editText } : todo
    );
    setTodos(updatedTodos);
    seteditingId(null);
    setEditingText('');
  };

  return (
    <>
      <h2>Simple Todo List</h2>
      <input type="text" placeholder="Enter Todo List" value={text} onChange={(e) => { setText(e.target.value) }} />
      <button onClick={handleAdd}>Add Todo</button>

      <ul>
        {todos.map((todo) => {
          <li key={todo.id}>
            {editingId === todo.id ?
              (<>
                <input type="text" placeholder="Enter Todo" value={editingText} onChange={(e) => { setEditingText(e.target.value) }} />
                <button onClick={() => { handleSave(todo.id) }}>Save</button>
              </>) : <>
                (
                  {todo.text}
                  <button onClick={() => handleEdit(todo.id, todo.text)}>Edit</button>
                  <button onClick={() => handleDelete(todo.id)}>Delete</button>
                )
              </>
            }
          </li>
        })}
      </ul>
    </>
  )
}

export default FC;
