import React, {useState, Suspense, lazy} from "react";

const LazyComponent = lazy(()=>import('./ControlledComponent'))
const FC = () => {
    let [show, isShow] = useState(false)
    return(
        <>
        <button onClick={()=>{isShow(!show)}}>Load component</button>
        <Suspense fallback={<h1>Loading</h1>}>
                <LazyComponent />
            </Suspense>
        </>
    )
}

export default FC;
