// lazy loading
// useCallback hook performence optimization technique



import React, {useState} from "react";

const FC = () =>{
    let [count, setCount] = useState(0)
    return(
        <>
        <h1>Count {count}</h1>
        <button onClick={()=>{setCount(++count)}}>INC</button>
        <button onClick={()=>{setCount(++count)}}>INC</button>
        <button onClick={()=>{setCount(++count)}}>INC</button>

        </>
    )
}

export default FC;
