//useState, useReducer, useRef, useContext, useEffect, useCallback, useMemo

import React, {useState, useEffect} from "react";
const FC = () => {
   let [count, setCount] = useState(0)
   let [effect, setEffect] = useEffect(0)
   useEffect(()=>{
    console.log('These gets logged based on dependency array')
   })
    return(
        <>
        <h1>Count : {count}</h1>
        <button onClick={()=>{setCount(++count)}}>INC</button>
        <button onClick={()=>{setEffect(++effect)}}>INC</button>
        </>
    )
}
export default FC;
