import { useRef } from 'react'

const useRefHook = () => {
  // creating a ref:
  const exampleRef = useRef()

  // setting the ref value:
  exampleRef.current = 'Learning about the useRef react hook'

  return (
    // accessing the ref value:

    <p>{exampleRef.current}</p>
  )
}

export default useRefHook;
