import { useState } from "react";

function StateInFunctionalComponent() {
    let [initial, update] = useState(0);
    return (
        <>
            <h2> State in Functional component - which dont have state so we use hook called useState</h2>
            <h2> Counter : {initial}</h2>
            <button onClick={() => {
                update(++initial);
            }}> click to Update</button>
            <button onClick={() => {
                update(--initial);
            }}>click to Decrese</button>
        </>
    )
}

export default StateInFunctionalComponent;
