//useState, useReducer, useRef, useContext, useEffect, useCallback, useMemo

import React, {useRef} from "react";


const FC = () => {
    let inputRef = useRef()
    return(
        <>
        <input ref={inputRef} type="text" placeholder="enter name" />
        <button onClick={()=>{alert(inputRef.current.value)}}>Get Name</button>
        </>
    )
}
export default FC;
