const reducerFunction = (state, input)=>{
    if(ac)
    return state + input;
}
const FC = () => {
    let [count, dispatch] = useReducer(reducerFunction, 0)
    return(
        <>
        <h1>The current value of the counter is : {count}</h1>
        <button onClick={()=>{dispatch(+1)}}>INC</button>
        <button onClick={()=>{dispatch(-1)}}>DEC</button>
        <button onClick={()=>{
            dispatch(reset)
            }}>Reset</button>
        </>
    )
}

export default FC;





















