import React, {createContext, useContext, useState} from "react";

const Countercontext = createContext()
const FC = () =>{
   let [count, setCounter] = useState(0)
    return(
        <Countercontext.Provider value={{count, setCounter}}>
            <Counter1 />
        </Countercontext.Provider>
    )
}

const Counter1 = () =>{
    let {count, setCounter} = useContext(Countercontext)
    return(
        <>
        <h1>The current value of the counter is : {count}</h1>
        <button onClick={()=>{setCounter(++count)}}>INC</button>
        </>
    )
}
export default FC;
