// export default FC;


// useEffect, useReducer, useref, usecontext, usecallback, usememo



import React, {useMemo, useCallback, useState} from "react";

const FC = () =>{
    let [a, setA] = useState(1);
    let [b, setB] = useState(1);

    const sum = useMemo(() => {
        return a + b;
    }, [a,b]);

    const mul = useCallback(() =>{
        return a * b
    }, [a, b]);

    return(
        <>
        <input type="text" value={a} onChange={e => {+e.target.value}} />
        <input type="text" value={b} onChange={e => {+e.target.value}} />
        <p>sum : {sum}</p>
        <p>mul : {mul()}</p>
        </>
    )
}

export default FC;
