// useEffect, useReducer, useref, usecontext, usecallback, usememo

import React, { useReducer} from "react";

const reducerFun = (state, action) => {
    return state + action;
}

const FC = () =>{
    let [count, dispatch] = useReducer(reducerFun, 0);
    return(
        <>
        <h1>The count is : {count}</h1>
        <button onClick={()=>{dispatch(+1)}}>INC</button>
        </>
    )
}
export default FC;
