import React, { useEffect } from "react";
import {  useReducer } from "react";

const updateFunction = (state, action) => {
    return state + action;
}
const FC = () => {
    let [count, dispatch] = useReducer(updateFunction, 0);
    return (
        <>
            <h1>The current value of the counter is : {count}</h1>
            <button onClick={()=>{
                dispatch(+1)
            }}>INC</button>

        </>
    )
}

export default FC;
