// functional component, classs component
// passing prop, default prop, prop check
// conditional rendering( ternary operator, if, ampercend)
// list and keys
// useState, useReducer, useEffect, useRef, useContext, useCallback, useMemo
// useCallback hook performence optimization technique
// controlled and uncontrolled component
// toggle
// api call axios and useeffect (ajax call)
// lazy loading
// create form and show form data
// custom hook (api calling)
// Todo list

import React, { useCallback, useMemo, useState, useRef } from "react";


const FC = () => {
    let [count, setCount] = useState(0)
    let [trigger, setTrigger] = useState(0)

    const passPropFunction = useCallback(() => {
        alert(`Num of times these trigger clicked is ${trigger}`)
    }, [trigger])
    return (
        <>
            <h1>Parent Component</h1>
            <h1>Count: {count}</h1>
            <h1>Trigger: {trigger}</h1>
            <button onClick={() => { setCount(++count) }}>INC Count</button>
            <button onClick={() => { setTrigger(++trigger) }}>INC Trigger</button>
            <ChildComponent prop={passPropFunction} />
        </>
    )
}

const ChildComponent = useMemo(({prop})=>{
    let render = useRef(0)
    render.current += 1
    return(
        <>
        <h1>Child Component</h1>
        <h1>These will be rendered everytime when trigger updated : {render.current}</h1>
        <button>Get T</button>
        </>
    )
})


export default FC;
