import React, {useState, useCallback, useMemo} from "react";

const FC = () =>{
    let [a, setA] = useState(1)
    let [b, setB] = useState(1)
    const Mul = useCallback(() => {
        return a * b;
    },[a,b])
    const Sum = useMemo(()=>{
        return a + b
    },[a,b]);
    return(
        <>
        <input type="text" placeholder="enter a value" value={a} onChange={(e)=>{setA(+e.target.value)}} />
        <input type="text" placeholder="enter b value" value={b} onChange={(e)=>{setB(+e.target.value)}} />
        <p>MUL : {Mul}</p>
        <p></p>
        
        </>
    )
}

export default FC;
