import React, {useCallback} from "react";


const FC = () => {
    const showMessage = () => {
        console.log('These message is gets logged')
    }
    return(
        <>
        <h1>These is the parent component</h1>
        <ChildComponent onClickMessage = {showMessage} />
        </>
    )
}

const ChildComponent = ({onClickMessage}) => {
    return(
        <>
        <h1>Child Component</h1>
        <button onClick={onClickMessage}>Get Mesage log</button>
        </>
    )
}

export default FC
