import React, {useRef} from "react";

const FC = () =>{
    const inputRef = useRef()
    const handleName = () =>{
        alert(`The name entered is: ${inputRef.current.value}`)
    }
    return(
        <>
        <input ref={inputRef} type="text" placeholder="Please Enter Name" />
        <button onClick={handleName}>GET Name Entered</button>
        </>
    )
}
export default FC;
