import React, {useEffect, useState} from "react";

const FC = () => {
    let [count, setCount] = useState(0)
    let [effect, setEffect] = useState(0)
    useEffect(()=>{
        console.log('These get logged based on the dependency array')
    })
    return(
        <>
        <h1>Count {count}</h1>
        <button onClick={()=>{setCount(++count)}}>INC Count</button>
        <button onClick={()=>{setEffect(++effect)}}>INC Effect</button>
        </>
    )
}
export default FC;
