// functional component, classs component
// passing prop, default prop, prop check
// conditional rendering( ternary operator, if, ampercend)
// list and keys
// useState, useReducer, useEffect, useRef, useContext, useCallback, useMemo
// useCallback hook performence optimization technique
// controlled and uncontrolled component
// api call axios and useeffect (ajax call)
// create form and show form data
// custom hook (api calling)
// toggle
// lazy loading
// Todo list

import React, { useState } from "react";

const FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form submitted:", email, password);
  };

  const validateEmail = (e) => {
    if (!e.target.value) {
      e.target.setCustomValidity("Email is required");
    } else {
      e.target.setCustomValidity("");
    }
  };

  const validatePassword = (e) => {
    if (!e.target.value) {
      e.target.setCustomValidity("Password is required");
    } else if (e.target.value.length < 6) {
      e.target.setCustomValidity("Password must be at least 6 characters");
    } else {
      e.target.setCustomValidity("");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="email"
        placeholder="Enter Email"
        value={email}
        required
        onChange={(e) => setEmail(e.target.value)}
        onInvalid={validateEmail}
        onInput={validateEmail}
      />

      <input
        type="password"
        placeholder="Enter Password"
        value={password}
        required
        onChange={(e) => setPassword(e.target.value)}
        onInvalid={validatePassword}
        onInput={validatePassword}
      />

      <button type="submit">Submit</button>
    </form>
  );
};

export default FC;
