import { useRef } from "react";

function UseRefHook() {
    const inputElement = useRef();

 

    return (
        <>
            <input type="text" ref={inputElement} />
            <button onClick={() =>{
                inputElement.current.focus();
                console.log(inputElement.current)
            }}
            >Focus Input</button>
        </>
    );
}

export default UseRefHook;
