import React, {useEffect} from "react";
import { useState } from "react";
const FC = () =>{
    let [counter, setCounter] = useState(0);
    let [effect, setEffect] = useState(0);
    useEffect(() =>{
        console.log('These will be logged based on the dependency array')
    },[])
    return(
        <>
            <h1>The current value of the counter is :{counter}</h1>
            <button onClick={()=>{setCounter(++counter)}}>INC</button>
            <button onClick={() =>{
                setEffect(++effect)
            }}>INC EFFECT</button>
        </>
    )
}
export default FC;
