import React, {useRef} from "react";


const FC = () => {
    const inputRef = useRef()
    return(
        <>
        <input type="text" placeholder="enter name" ref={inputRef} />
        <button onClick={()=>{console.log('The name entered is :',inputRef.current.value)}}>Get Name</button>
        </>
    )
}
export default FC;
