import React, {useCallback, useMemo, useState} from "react";

const FC = () => {
    let [a, setA] = useState(1);
    let [b, setB] = useState(1);
    const mul = useCallback(() =>{
        return a * b;
    },[a,b]);
    const sum = useMemo(()=>{
        return a + b;
    },[a,b]);
    return(
        <>
        <input type="text" placeholder="enter value of A" value={a} onChange={e => setA(+e.target.value)} />
        <input type="text" placeholder="enter value of B" value={b} onChange={e => setB(+e.target.value)} />
        <p>MUL : {mul()}</p>
        <p>SUM : {sum}</p>
        </>
    )
}

export default FC;
