// api call axios and useeffect (ajax call)
// create form and show form data
// custom hook (api calling)
// toggle
// lazy loading
// useCallback hook performence optimization technique

import React, {useEffect, useState} from "react";

const FC = () => {
  let [counter, setCounter] = useState(0)
  let [effect, setEffect] = useState(0)
  useEffect(()=>{
    console.log('These gets logged based on the dependency array')
  },[e])
  return(
    <>
    <h1>Counter : {counter}</h1>
    <h1>Effect : {effect}</h1>
    <button onClick={()=>{setCounter(++counter)}}>INC Counter</button>
    <button onClick={()=>{setEffect(++effect)}}>INC Effect</button>
    </>
  )
}

export default FC
