// useState, useEffect, useReducer, useRef, Usecallback , usememo , useContext

import React, {useContext, createContext} from "react";
import { useState } from "react";

const FC = () =>{
    const CounterContext = createContext()
    let [count, updateCount] = useState(0);
    return(
        <>
        <CounterContext.Provider value={{count, updateCount}}>
            <ChildComponent />
        </CounterContext.Provider>
        </>
    )
}


const ChildComponent = () =>{
    return(
        <>
        let [count, updateCount] = useContext(CounterContext);
        <button onClick={()=>{updateCount(++count)}}>Inc</button>
        </>
    )
}
export default FC;
