import React from "react";
import { useState } from "react";

const StateInFunctionalComponent = () =>{
    let [initial, updateFun] = useState(0);
    return(
        <>
        <h1>Hi these is the usestate in functional component</h1>
        <h3>the current value of the counter is : {initial}</h3>
        <button onClick={()=>{
            updateFun(++initial)
        }}>
            INCREMENT
        </button>
        <button onClick={()=>{
            updateFun(--initial)
        }}>
            DECREMENT
        </button>
        <button onClick={()=>{
            updateFun(0)
        }}>
            RESET
        </button>
        </>
    )
}

export default StateInFunctionalComponent;
