import React, {useContext, createContext, useState} from "react";

const CountContext = createContext()
const FC = () => {
    let [count, setCount] = useState(0);
    return(
        <>
        <CountContext.Provider value={{count,setCount}} >
            <ChildComponent />
        </CountContext.Provider>
        </>
    )
}
export default FC;
