import React from "react";
import './App.css';

export class App extends React.Component{
    state = {
        Name:"Iphone 14Plus",
        cost: 79999

    }
    constructor(){
        super();
        this.updatePrice = this.updatePrice.bind(this)
    }
    render(){
        return(
            <div>
                <h1>Hai , Welcome to class componenet state concept</h1>
                <h2>Phone  : {this.state.Name}</h2>
                <h2>Price: {this.state.cost}</h2>
                <input  id='input' type="number"></input>
                <button onClick={this.updatePrice}>Enter to which price you make a deal</button>
            </div>
        )
    }
    updatePrice(){
        let p = document.getElementById('input').value;
        this.setState({
            cost:p
        })
    }
}
