import React, {useRef} from "react";

const FC = () =>{
    const inputRef = useRef()
    const handleText = () => {
        console.log('the value entered is :',inputRef.current.value)
    }
    return(
        <>
        <h1>These is the react useref hook example</h1>
        <input type="text" ref={inputRef} />
        <button onClick={handleText}>Get the name entered</button>
        </>
    )
}
export default FC;
