import React, {useEffect} from "react";
import { useState } from "react";

const FC = () => {
    let [counter, setCounter] = useState(0)
    let [effect, setEffect] = useState(0)

    useEffect(()=>{
        console.log('These gets logged');
    },)

    return( 
        <>
        <h1>The current value of the Counter : {counter}</h1>
        <button onClick={()=>{setCounter(++counter)}}>INC Counter</button>
        <button onClick={setEffect(++effect)}>INC Effect</button>
        </>
    )
}
export default FC;



















