import React, {useState, createContext, useContext} from "react";
const CountContext = createContext()
const FC = () =>{

    let [current, setCurrent] = useState(0)
    return(
        <CountContext.Provider value={{current, setCurrent}} >
            <counter />
        </CountContext.Provider>
    )
}

const counter = () =>{
    const {current, setCurrent} = useContext(CountContext);
    return(
        <>
        <h1>The current value of the counter is :{current}</h1>
        <button onClick={()=>{
            setCurrent(++current)
        }}>Increment</button>
        </>
    )
}
export default FC;
