import React, { useContext, createContext, useState } from "react";

const Countercontext = createContext()

const FC = () => {
    let [count, setCount] = useState(0)
    return (
        <>
            <Countercontext.Provider value={{ count, setCount }} >
                <ChildComponent />
            </Countercontext.Provider>
        </>
    )
}
export default FC;
