import React from "react";
import './App.css';
 
export class App extends React.Component{
    state={
        counter:0
    }
    render(){
        return(
            <div>
                <h1>Hai , Welcome to class componenet state concept</h1>
                <h2>Counter Values is : {this.state.counter}</h2>
                <button onClick={()=>{
                    this.setState({
                        counter: this.state.counter + 1
                    })
                }}>click to update the counter value</button>
            </div>
        )
    }
}
