import { useState } from "react";

const FC = () =>{
    let [counter, setCounter] = useState(0)
    return(
        <>
        <h1>The current value of the counter is : {counter}</h1>

        <button onClick={()=>{setCounter(++counter)}}>INC</button>
        </>
    )
}
export default FC;
