import React from "react";
import { useState,useEffect } from "react";

const StateInFunctionalComponent = () =>{
    let [currrentValue, updateFun] = useState(0);
    let [counter, updateCounter] = useState(0);
    useEffect(() => {
        console.log('These get logged everytime when the dependency is updated')
    }, [])
    return(
        <>
        <h1>Hi these is the  in functional component : {currrentValue}</h1>
        <button onClick={()=>{
            updateFun(++currrentValue)
        }}>
            INCREMENT
        </button>
        <button onClick={()=>{
            updateCounter(++counter)
        }}>
            updateCounter To Log 
        </button>
      </>
    )
}

export default StateInFunctionalComponent;
