// export default FC;

//hooks(useState, useeffect, usereducer,useref,usecontext,usecallback,usememo)

import React, {useReducer} from "react";

const updateFunction = (state, action) =>{
    return state+action
}
const FC = () =>{
    let [current, dispatch] = useReducer(updateFunction, 0);
    const initialState = {
        current : 0
    }
    return(
        <>
        <h1>The current value of the counter is :{current}</h1>
        <button onClick={()=>{
            dispatch(+1)
        }}>INC</button>
        <button onClick={()=>{
            dispatch(-1)
        }}>DEC</button>
        <button onClick={()=>{
            dispatch(initialState.current)
        }}>RESET</button>
        </>
    )
}
export default FC;
