import React, {useRef} from "react";

const FC = () => {
    let inputRef = useRef()
    return(
        <>
        <input type="text" ref={inputRef} placeholder="Enter Name" />
        <button onClick={()=>{alert(`the name entered is ${inputRef.current.value}`)}}>GET Name</button>
        </>
    )
}
export default FC;
