import React from "react";
import { useState } from "react";

const StateInFunctionalComponent = () => {
    let [current, updateFun] = useState(0)
    return (
        <>
            <h1>The current value of the state is : {current}</h1>
            <button onClick={() => {
                updateFun(++current)
            }}>
                Inc
            </button>
            <button onClick={() => {
                updateFun(--current)
            }}>
                DECREMENT
            </button>
            <button onClick={()=>{
                updateFun(0)
            }}>
                Reset
            </button>

        </>
    )
}

export default StateInFunctionalComponent;
