// export default FC;

import React, { useState } from "react"
const FC = () => {
    let [currentValue, updateValue] = useState(0);
    return (
        <>
            <h1>these is the counter value is : {currentValue}</h1>
            <button onClick={() => {
                updateValue(+1)
            }}>
                Increment
            </button>
            <button onClick={() => {
                updateValue(-1)
            }}>
                Decrement
            </button>
            <button onClick={() => {
                updateValue(0)
            }}>
                Reset
            </button>
        </>
    )
}

export default FC;
