import React, { useEffect, useState } from "react";


const FC = () => {
    let [count, setCount] = useState(0)
    let [effect, setEffect] = useState(0)

    useEffect(() => {
        alert('These gets exectuted and logged based on the dependency array')
    },[effect])

    return (
        <>
            <h1>Count : {count}</h1>
            <h1>Effect: {effect}</h1>
            <button onClick={() => { setCount(++count) }}>INC Count</button>
            <button onClick={() => { setEffect(++effect) }}>INC Effect</button>
        </>
    )
}


export default FC;
