// export default FC;

// hooks(useState, useeffect, usereducer,useref,usecontext,usecallback,usememo)


import React, {useCallback, useMemo, useState} from "react";

const FC = () =>{
    let [a, setA] = useState(1)
    let [b, setB] = useState(1)
    const sum = useMemo(() =>{
        return a + b;
    },[a,b]);
    const mul = useCallback(() =>{
        return a * b;
    },[a,b]);
    return(
        <>
        <input type="text" value={a} onChange={(e)=>{setA(+e.target.value)}} />
        <input type="text" value={b} onCanPlay={(e)=>setB(+e.target.value)} />
        <p>sum : {sum}</p>
        <p>mul : {mul()}</p>
         </>
    )
}
export default FC;
