// export default FC;

// useState, useEffect, useReducer, useRef, Usecallback , usememo , useContext

import React from "react";
import { useState, useCallback, useMemo } from "react";

const FC = () =>{
    let [a, setA] = useState(1);
    let [b, setB] = useState(1);

    const sum = useMemo(() => {
        return a + b;
    }, [a,b]);

    const mul = useCallback(() => {
        return a * b;
    }, [a,b]);

    return(
        <>
       <input type="text" value={a} onChange={e => setA(+e.target.value)} />
       <input type="text" value={b} onChange={e => setB(+e.target.value)} />
       <p> The sum is : {sum}</p>
       <p> The mul is : {mul()}</p>
       
        </>
    )
}
export default FC;
