//useState, useReducer, useRef, useContext, useEffect, useCallback, useMemo

import React, {useReducer} from "react";
const reducerFunction = (state, action) => {
    if(action.type === 'res') return 0
}
const FC = () =>{
    let [count, dispatch] = useReducer(reducerFunction,0)
    return(
        <>
        <h1>count : {count}</h1>
        <button onClick={()=>{setCount(++count)}}>INC</button>
        <button onClick={()=>{setCount(--count)}}>DEC</button>
        <button onClick={()=>{setCount(0)}}>RES</button>
        </>
    )
}
export default FC;
