// export default FC;


// hooks, making a api call, form data

import React, {useEffect, useState} from "react";

const FC = () =>{
    let [count, setCount] = useState(0);
    let [dependency, setDependency] = useState(0);

    useEffect(() => {
        console.log('These gets logged when dependency updated');
    });
    // [] => if empty array then only gets rendered at first time
    // if no second argument everytime when state rendered
    // if given second arg and given some var then will update everytime when that updated
    return(
        <>
        <h1>count:{count}</h1>
        <button onClick={()=>{
            setCount(++count)
        }}>INC</button>
        <button onClick={()=>{
            setDependency(++dependency)
        }}>Update dependency</button>
        </>
    )
}

export default FC;
