// functional component, classs component
// passing prop, default prop, prop check
// conditional rendering( ternary operator, if, ampercend)
// list and keys
// useState, useReducer, useEffect, useRef, useContext, useCallback, useMemo
// controlled and uncontrolled component
// api call axios and useeffect (ajax call)
// create form and show form data
// custom hook (api calling)
// toggle
// lazy loading
// useCallback hook performence optimization technique


import React, { useState } from 'react';

export default function TodoApp() {
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState('');

  // Add new todo
  const handleAdd = () => {
    if (text.trim()) {
      setTodos([...todos, { id: Date.now(), text, isEditing: false }]);
      setText('');
    }
  };

  // Delete todo
  const handleDelete = (id) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  // Start editing
  const startEdit = (id) => {
    setTodos(todos.map(todo =>
      todo.id === id ? { ...todo, isEditing: true } : todo
    ));
  };

  // Save edited text
  const handleSave = (id, newText) => {
    setTodos(todos.map(todo =>
      todo.id === id
        ? { ...todo, text: newText, isEditing: false }
        : todo
    ));
  };

  return (
    <div>
      <h2>Todo List1</h2>
      <input
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Add a todo"
      />
      <button onClick={handleAdd}>Add</button>

      <ul>
        {todos.map(todo => (
          <li key={todo.id}>
            {todo.isEditing ? (
              <>
                <input
                  defaultValue={todo.text}
                  onBlur={(e) => handleSave(todo.id, e.target.value)}
                  autoFocus
                />
              </>
            ) : (
              <>
                {todo.text}{' '}
                <button onClick={() => startEdit(todo.id)}>Edit</button>
                <button onClick={() => handleDelete(todo.id)}>Delete</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
