import React, { useCallback, useState } from "react";



const FC = () => {

    const showMessage = () => {
        alert('These gets logged')
    }
    return (
        <>
        <h1>Parent component</h1>
        <ChildComponent onSend={showMessage} />
        </>
    )
}

const ChildComponent = React.memo(({onSend})=>{
    return(
        <>
        <h1>Child component</h1>
        <button>Get Parent Al</button>
        </>
    )
})

export default FC;
