// functional component, classs component
// passing prop, default prop, prop check
// conditional rendering( ternary operator, if, ampercend)
// list and keys
// useState, useReducer, useEffect, useRef, useContext, useCallback, useMemo
// useCallback hook performence optimization technique
// controlled and uncontrolled component
// toggle
// api call axios and useeffect (ajax call)
// lazy loading
// create form and show form data
// custom hook (api calling)
// Todo list



import React, { useState } from "react";


const FC = () => {
    let [todos, setTodos] = useState([])
    let [text, setText] = useState('')
    let [editId, setEditid] = useState(null)
    let [editText, setEditText] = useState('')

    const handleAdd = () => {
        if (text.trim()) {
            let newTodo = { id: Date.now(), text }
            setTodos([...todos, newTodo])
            setText('')
        }
    }

    const handleSave = (id) => {
        const updatedTodo = todos.map((todo) => {
            return todo.id === id ? {editId, text} : todo
        })
        setTodos(updatedTodo)
        setEditid(null)
        setEditText('') 
    }

    const handleEdit = (id, currentText) => {
        setEditid(id)
        setEditText(text)
    }

    const handleDelete = (id) => {
        setTodos(todos.filter((todo) => {
            return todo.id !== id
        }))
    }

    return (
        <>
            <input type="text" placeholder="Enter Todo" value={text} onChange={(e) => { setText(e.target.value) }} />
            <button onClick={handleAdd}>Add Todo</button>

            <ul>
                {todos.map((todo) => {
                    return <li key={todo.id}>
                        {editId === todo.id ? (
                            <>
                                <input type="text" placeholder="Edit Todo" value={editText}
                                    onChange={(e) => { setText(e.target.value) }} />
                                <button onClick={() => { handleSave(todo.id) }}>Save</button>
                            </>
                        ) : (
                            <>
                                {todo.text}
                                <button onClick={() => { handleEdit(todo.id, todo.editText) }}>Edit</button>
                                <button onClick={() => { handleDelete(todo.id) }}>Delete</button>
                            </>
                        )}
                    </li>
                })}
            </ul>
        </>
    )
}

export default FC;
