import React, { useReducer } from "react";

const reducerFunction = (state, action) => {
    if(action.type === 'RES') return 0;
    return state + action
}

const FC = () => {
    let [current, dispatch] = useReducer(reducerFunction,0)
    return(
        <>
            <h1>Count : {current}</h1>
            <button onClick={()=>{dispatch(+1)}}>INC</button>
        </>
    )
}

export default FC
