import React, {useState} from "react";

const FC = () =>{
    let [count, setCount] = useState(0)
    return(
        <>
        <h1>hey Mansoor</h1>
        <h1>The current value of the counter is : {count}</h1>
        <button onClick={()=>{
            setCount(++count)
        }}>INC</button>
        <button onClick={()=>{
            setCount(--count)
        }}>DEC</button>
        </>
    )
}

export default FC;
